import React from "react"
import { Link } from "react-router-dom"

import styles from "./index.module.scss"
import classNames from "classnames/bind"
import StoreButton, { RightArrow } from "./storeButton"
const cx = classNames.bind(styles)

const CategoriesText = props => {
    return (
        <h4 className={cx("categoriesText")}>
            <div className={cx("categoriesTextTitle")}>
                {props.title}
            </div>
            <Link className={cx("categoriesTextLink")} to="#fiction">
                Fiction
            </Link>
            <StoreButton>
                {"More categories "}
                <RightArrow />
            </StoreButton>
        </h4>
    )
}

export default CategoriesText
