import React from "react"

import styles from "./index.module.scss"
import classNames from "classnames/bind"
const cx = classNames.bind(styles)

const ResponsiveRow = props => {
    return (
        <div className={cx({
            "responsiveRow": true,
            ...props.className
        })} {...props}>
            {props.children}
        </div>
    )
}

export default ResponsiveRow
