import React from "react"
import { useParams } from "react-router"
import Navbar from "../common/navbar"
import NavBreadcrumb from "../common/navBreadcrumb"
import ResponsiveRow from "../common/responsiveRow"
import StoreButton, { RightArrow } from "../common/storeButton"
import NotFoundPage from "../notFound/notFoundPage"
import ProductCard, { StarRating, TestProduct } from "../products/productCard"

const ProductDetailPage = props => {
    const params = useParams()

    if (!params.id) {
        return <NotFoundPage />
    }

    const prod = props.product || TestProduct

    return (
        <div>
            <Navbar/>
            <NavBreadcrumb/>
            <div>
                <h1> {prod.title}</h1>
                <StarRating star={prod.averageRating} />
                (<span>{prod.reviewCount} customer reviews</span>)
            </div>
            <p>
                {prod.description}
            </p>
            <ProductInfo product={prod} />
            <div>
                <h4>Related products</h4>
                <StoreButton variant="secondary">
                    {"More products "}
                    <RightArrow />
                </StoreButton>
            </div>
            <ResponsiveRow>
                <ProductCard
                    border={false}
                    product={TestProduct}
                />
                <ProductCard
                    border={false}
                    product={TestProduct}
                />
                <ProductCard
                    border={false}
                    product={TestProduct}
                />
                <ProductCard
                    border={false}
                    product={TestProduct}
                />
            </ResponsiveRow>
        </div>
    )
}

export default ProductDetailPage
