import React from "react"
import { useParams } from "react-router"
import Navbar from "../common/navbar"
import NavBreadcrumb from "../common/navBreadcrumb"
import ResponsiveRow from "../common/responsiveRow"
import StoreButton, { RightArrow } from "../common/storeButton"
import NotFoundPage from "../notFound/notFoundPage"
import ProductCard, { StarRating, TestProduct } from "../products/productCard"
import ProductInfo from "./productInfo"

import styles from "./index.module.scss"
import classNames from "classnames/bind"
const cx = classNames.bind(styles)

const ProductDetailPage = props => {
    const params = useParams()

    if (!params.id) {
        return <NotFoundPage />
    }

    const prod = props.product || TestProduct

    return (
        <div>
            <Navbar/>
            <NavBreadcrumb path={[
                {
                    name: "Homepage",
                    link: "/"
                },
                {
                    name: "Fiction",
                    link: "/fiction"
                },
                {
                    name: prod.name,
                    link: "/fiction/product/" + prod.id
                }
            ]}
            />
            <div className={cx("detailContainer")}>
                <div className={cx("detailText")}>
                    <div>
                        <h1> {prod.title}</h1>
                        <StarRating star={prod.averageRating} />
                        (<span>{prod.reviewCount} customer reviews</span>)
                    </div>
                    <p>
                        {prod.description}
                    </p>
                    <ProductInfo product={prod} />
                </div>
                <img src={prod.coverLink} alt={prod.title} />
            </div>
            <div>
                <h4>Related products</h4>
                <StoreButton variant="secondary">
                    {"More products "}
                    <RightArrow />
                </StoreButton>
            </div>
            <ResponsiveRow>
                <ProductCard
                    border={false}
                    product={TestProduct}
                />
                <ProductCard
                    border={false}
                    product={TestProduct}
                />
                <ProductCard
                    border={false}
                    product={TestProduct}
                />
                <ProductCard
                    border={false}
                    product={TestProduct}
                />
            </ResponsiveRow>
        </div>
    )
}

export default ProductDetailPage
